template.js:
devOrientHandler = function(ev) {
    //console.log("OL: ", ev);
    data = {alpha: ev.alpha, beta: ev.beta, gamma: ev.gamma};
    publishFeed("Orientation", {alpha: ev.alpha, beta: ev.beta, gamma: ev.gamma});
    Session.set("DeviceOrientation", data);
};



Template[name].onRendered(function(){
    Session.set("DeviceOrientationFeed", this.feed ? this.feed : "dummyfeed")
});


Template[name].helpers({
    orientationSupported: function(){
        if (window.DeviceOrientationEvent && Session.get("DeviceOrientation").alpha != null) {
            return "Gyro outputs on";
        } else {
            return "Gyro output not supported."
        }
    },
    orientationData: function(){
        return Session.get("DeviceOrientation");
    }
});



if (window.DeviceOrientationEvent) {
  // Listen for the event and handle DeviceOrientationEvent object
  window.addEventListener('deviceorientation', devOrientHandler, false);
}
