Template[name].onCreated(function(tg){
     this.state = new ReactiveDict();
     this.data.legend = this.data.legend || "\"Reactor Level\"";
     this.data.values = this.data.values || "23,25,23,22,21,30,35,22,32,37,38,39,23,10,11,33,34"
});



Template[name].onRendered(function(){
     mv = this.data.maxvalue ? this.data.maxvalue : null;
     this.state.set("maxvalue", mv)
     this.state.set("minvalue", this.data.minvalue)
         //console.log("CRE", this, mv, this.data);
})

Template[name].helpers({
    json_parse: function(str){
        if(!str) {
            return;
        }
        var vals = str.split(',');
        //Save current value unmapped.
        Template.instance().state.set("currentValue", vals[vals.length-1])
        mv = Template.instance().state.get("maxvalue");
        minv = Template.instance().state.get("minvalue");
        console.log("MV: ", mv, minv)
        // Autoscale if no max set.
        if(!mv) {
            pd  = vals.map(function(v){
                return parseFloat(v) || 0;
            });
            mv = Math.max(...pd);
            minv = Math.min(...pd);
            Template.instance().state.set("max", mv);
            Template.instance().state.set("min", minv);
            diff = mv - minv;
            mv += diff/3;
            minv -= diff/3;
            console.log("Autoscaling ", pd, mv, minv)
        }
        ra =  vals.map(function(v){

            return   mapValue(v, minv, mv, 0, 250);
        })
        return ra;
    },
    legend: function(){
        return Template.instance().data.legend;
    },
    debug: function(v){
      console.log("DEBUG: ",v)  ;
    },
    previndex: function(){

      return this.index-1;
    },
    prevvalue: function(){
        pd  = Template.parentData();

        return pd[this.index-1];
    },
    revvalue: function(){
      return 250-this.value;  
    },
    step: function(num, length){

        return num * 500/length;
    },
    minval: function(){
        pd  = Template.parentData();
        //console.log(this.value, (pd))
        return this.value ==  Math.min.apply(null, pd);
    },
    maxval: function(){
        pd  = Template.parentData();
        //console.log(this.value, (pd))
        return this.value ==  Math.max.apply(null, pd);
    },
    currentval: function(){
                // console.log("CV :", this)
        v = this[this.length-1];

        return  Template.instance().state.get("currentValue");
    },
    max: function() {
       return  Template.instance().state.get("max");
    },
    min: function() {
        return  Template.instance().state.get("min");
    },
    autoscaling: function(){
        return Template.instance().state.get("maxvalue") ? "fixed scale" : "autoscaled";
    }

});
