Template[name].onCreated(function(){
     //console.log("MV ", this , this.data.maxValue)
    this.data.maxvalue = parseFloat(this.data.maxvalue) || 101;
    this.data.minvalue = parseFloat(this.data.minvalue) || 0;
    this.data.legend = this.data.legend || "Warp Factor"
    this.data.value = this.data.value || 23;
    this.data.calculations = this.data.calculations || JSON.stringify({"min":10, "max": 80, "avg": 45});
});


Template[name].helpers({
      mappedValue: function(value) {
         //console.log("T270", value,  Template.instance())
        m =  mapValue(value, Template.instance().data.minvalue, Template.instance().data.maxvalue, 0, 270)-135
        return m;
    },
    minmaxscaled: function( json ){
        calc = JSON.parse(json)
        //console.log("JS", calc, this)
        if(!calc ) {
            return;
        }

        maxValue = this.maxvalue;
        minValue = this.minvalue;
        //console.log("MM ",  calc, maxValue, Template.instance().data )
        ret = {}
       // ret.payload = mapValue(this.payload, 0, 120, 0, 200);
        ret.diff = mapValue(calc.diff, minValue, maxValue, 0, 270);
        ret.min =  mapValue(calc.min, minValue, maxValue, 0, 270)-135;
        ret.max = mapValue(calc.max, minValue, maxValue, 0, 270)-135;
        ret.avg =  mapValue(calc.avg, minValue, maxValue, 0, 270)-135;
        ret.diffavg = mapValue(calc.diffavg, minValue, maxValue, 0, 270);
        //console.log("REG", ret)
        return ret;
    },
    dirColor: function(){
        return this.diffavg > 0 ? "green" : "red";
    },
    debug: function(p){
        console.log("DEB: ", p, this)
    }
});
